def first(journalists)
puts "Combien de twitter handle sont présents dans le tableau ?"
print "\n"
puts "Il y a #{journalists.length} Twitter handle dans cet array."
print "\n"
puts "Continuez en tapant sur entrée"
    gets.chomp
end

def second(journalists)
    puts "Quel handle compte le moins de caractères ?"
    print "\n"
    puts journalists.min {|a, b| a.size <=> b.size}
    print "\n"
    puts "Continuez en tapant sur entrée"
    gets.chomp
 end

def third(journalists)
    puts "Combien de handle font cinq caractères (hors @)?"
    print "\n"
    puts "Il y a #{journalists.count {|five_char| five_char.size == 6}} handles de cinq caractères"
    print "\n"
    puts "Continuez en tapant sur entrée"
    gets.chomp
end

def fourth(journalists)
    puts "Combien de twitter handle commençent par une majuscule ?"
    print "\n"
    puts "Il y a donc #{journalists.count {|e| e =~ /^@[A-Z]/}} handles qui commence par une majuscule."
    print "\n"
    puts "Continuez en tapant sur entrée"
    gets.chomp
end
    
def fifth(journalists)
    puts "Les twitter handle classés par ordre alphabétique : "
    print "\n"
    puts journalists.sort_by(&:upcase)
    print "\n"
    puts "Continuez en tapant sur entrée"
    gets.chomp
end

def sixth(journalists)
    puts "Les twitter handle classés par ordre croissant : "
    print "\n"
    puts journalists.sort_by! {|element| element.size }
    print "\n"
    puts "Continuez en tapant sur entrée"
    gets.chomp
end

def seventh(journalists)
    puts "Ou se trouve le twitter handle : @epenser ?"
    print "\n"
    puts "Le handle @epenser est en position : #{journalists.index("@epenser")} !"
    print "\n"
    puts "Continuez en tapant sur entrée"
    gets.chomp
end
    
def eighth(journalists)
    puts "Les twitter handle triés par nombre de caractères :"
    print "\n"
    y = 2
    i = 33
    a = 1
    while y <= i
        if (journalists.count {|x| x.size == y}) !=0
        puts "Il y a #{journalists.count {|x| x.size == y}} handle(s) avec #{a} caractère(s)."
        end
        y += 1
        a += 1
    end
    print "\n"
    puts "Appuyez pour quitter !"
    gets.chomp
end

